[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "."}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<!DOCTYPE html>
<html>
<head>
<meta name="viewport" content="width=device-width, initial-scale=1">
<style>
* {
  box-sizing: border-box;
}

body {
  font-family: Arial, Helvetica, sans-serif; 
}

/* Float four columns side by side */
.column {
  float: left;
  width: 50%;
  padding: 0 1px;
  margin-bottom:30px;
  margin-top:00px;
}

/* Remove extra left and right margins, due to padding */
.row {margin: 0 -1px;}

.a {
  background:#ffffff;
  padding:5px;
  margin:1px 0;
  height:80px;
  width:auto;
  text-align:center;
}
.b{
  text-align:center;
  height:85px;
  width:auto;
  margin:0px 0;
}
.c{
    height:50px;
  background:#ffffff;
  padding:3px;
}

.p{
  font-weight: bold;
color:  #e8e80b;
font-size: 12pt;
text-align: center;
text-transform: uppercase;
height:90px;
width:auto;
}
.p2{
  font-weight: bold;
color: #e8e80b;
font-size: 16pt;
text-align: center;
text-transform: uppercase;
height:20px;
width:auto;
}
.p5{
font-family: "Lucida Console", "Courier New", monospace;
color:  #e8e80b;
font-size: 8pt;
text-align: center;
height:8px;
width:auto;
}
.imgwrapper{
float: left;
  width: 100%;
  text-align:center;
  height:90px;
}
.column {
  float:left;
  width: 50%; 
  padding: 2px;
  /* height: 305px;  Should be removed. Only for demonstration */
}
/* Responsive columns */
@media screen and (max-width: 600px) {
  .column {
    width: 100%;
    display: block;
    margin-top: 0px
  }
}

/* Style the counter cards */
.card {
    border: solid 0px white;
    height: 72mm;
    width: 98mm;
    margin-bottom:8mm;
    margin-top:8mm;
    padding: 1px;
    background-color: #a8003a;
}
.p.ex1 {
  margin-top: 0px;
  text-align: center;
  font-size: 12px;
}
</style>
</head>

<body>
<div class="row">
  {% for item in doc.item_tag_details %}
  <div class="column">
  <div class="card">
    <div class="row">
      <div class="column">
          <div class="imgwrapper">
        <img src="/files/llogo.png" >
        <p class="p.ex1">
        <img class="a" src="https://qrcode.tec-it.com/API/QRCode?data=https://erp.premiumcellars.ae/app/website-item/{{item.website_item_name}}"/>
        </p>
        {% set barcode = frappe.db.get_value("Item Barcode",{"parent":item.item_code,"posa_uom":"Pcs"} , "barcode") %}
        <p class="p.ex1">
            <img class="c" alt='Barcode {{doc.name}}'src='https://barcode.tec-it.com/barcode.ashx?data={{barcode or item.item_code}}&code=Code128&multiplebarcodes=false&translate-esc=true&unit=Fit&dpi=96&imagetype=Gif&rotation=0&color=%23000000&bgcolor=%23ffffff&codepage=Default&qunit=Mm&quiet=0'?draggable="false"/>
        </p>
        <p class="p5">{{item.item_code}}</p> 
        </div>
      </div>
      <div class="column">
          <div class=imgwrapper>
              {% set image = frappe.db.get_value("Item",{"name":item.item_code} , "image") or "/files/LIQUOR1.png" %}
        <!--<p class="p">{{item.description |upper}}</p>-->
        <p class="p">{{item.description }}<br>({{item.uom }})</p> 
        <img class="b" src="{{image}}">
        {% set price = frappe.db.get_value("Item Price",{"item_code":item.item_code,"price_list":"Premium Shop Selling"} , "price_list_rate") %}
        <p class="p2" style="text-align: center;">PRICE</p>
        <p style="font-size:16pt;font-weight:bold;color:#e8e80b;text-align: center;">AED {{ item.price or 0.00}}0 </p>
        </div>
        <!-- <p class="p">{{item.item_code}}</p> -->
      </div>
    </div>
    </div>
  </div>

  {%- endfor -%} </div>

</body>
</html>

"}]
