[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "<div class="print-heading">				<h2><div>Item Tag Print</div><br><small class="sub-heading">{{ doc.name }}</small>				</h2></div>"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<!DOCTYPE html>
<html>
<head>
<meta name="viewport" content="width=device-width, initial-scale=1">
<style>
* {
  box-sizing: border-box;
}

body {
  font-family: Arial, Helvetica, sans-serif; 
}

/* Float four columns side by side */
.column {
  float: center;
  width: 100%;
  padding: 0 10px;
}

/* Remove extra left and right margins, due to padding */
.row {margin: 0 -10px;}

.a {
  background:#ffffff;
  padding:5px;
  margin:10px 0;
  height:105px;
  width:auto;
  text-align:center;
}
.b{
  text-align:center;
  height:135px;
  width:auto;
  margin:5px 0;
}
.c{
    height:60px;
  background:#ffffff;
  padding:3px;
}

.p{
  font-weight: bold;
color: #e8e80b;
font-size: 12pt;
text-align: center;
text-transform: uppercase;
}
.imgwrapper{
float: left;
  width: 100%;
  text-align:center;
}
.column {
  float:left;
  width: 100%; 
  padding: 0;
  /* height: 305px;  Should be removed. Only for demonstration */
}
/* Responsive columns */
@media screen and (max-width: 600px) {
  .column {
    width: 100%;
    display: block;
    margin-bottom: 20px;
  }
}

/* Style the counter cards */
.card {
    height: 45mm;
    width: 200mm;
    border: solid 10px white;
    padding: 16px;
    width: 100%;
    background-color: #a8003a;
}
.p.ex1 {
  margin-top: 15px;
  text-align: right;
}
</style>
</head>

<body>
<div class="row">
  {% for item in doc.item_tag_details %}
  <div class="column">
  <div class="card">
    <div class="row">
      <div class="column">
          <div class="imgwrapper">
        <img src="/files/llogo.png" >
        <p class="p.ex1">
        <img class="a" src="https://qrcode.tec-it.com/API/QRCode?data=https://demo.richmondspirits.com/app/website-item/{{item.website_item_name}}"/>
        </p>
        <p class="p.ex1">
            <img class="c" alt='Barcode {{doc.name}}'src='https://barcode.tec-it.com/barcode.ashx?data={{item.item_code}}&code=Code128&multiplebarcodes=false&translate-esc=true&unit=Fit&dpi=96&imagetype=Gif&rotation=0&color=%23000000&bgcolor=%23ffffff&codepage=Default&qunit=Mm&quiet=0'?draggable="false"/>
        </p>
        </div>
      </div>
      <div class="column">
          <div class=imgwrapper>
        <p class="p">{{item.description |upper}}</p>
        <img class="b" src="{{item.website_image}}">
        <p class="p" style="text-align: center;">PRICE</p>
        <p style="font-size:19pt;font-weight:bold;color:white;text-align: center;">AED 000.00</p>
        </div>
        <!-- <p class="p">{{item.item_code}}</p> -->
      </div>
    </div>
    </div>
  </div>

  {%- endfor -%} 
</div>

</body>
</html>

