<html>
<head>
<meta name="viewport" content="width=device-width, initial-scale=1">
<style>
* {
  box-sizing: border-box;
}

body {
  font-family: Arial, Helvetica, sans-serif; 
}

/* Float four columns side by side */
.column {
  float: left;
  width: 50%;
  padding: 0 1px;
  margin-bottom:10px;
  margin-left:0px;
}

/* Remove extra left and right margins, due to padding */
.row {margin: 0 -1px;}

.a {
  background:#ffffff;
  padding:5px;
  margin:1px 0;
  height:80px;
  width:auto;
  text-align:center;
}
.b{
  text-align:center;
  height:85px;
  width:auto;
  margin:0px 0;
}
.c{
    height:14mm;
    width:auto;
  background:#ffffff;
  padding:3px;
}

.p{
  font-weight: bold;
color:  #e8e80b;
font-size: 11pt;
text-align: center;
text-transform: uppercase;
height:50px;
width:auto;
}
.p2{
  font-weight: bold;
color:  #e8e80b;
font-size: 10pt;
text-align: center;
text-transform: uppercase;
height:15px;
width:auto;
}
.p5{
font-family: "Lucida Console", "Courier New", monospace;
color: #e8e80b;
font-size: 12pt;
text-align: center;
height:8px;
width:auto;
}
.p6{
font-family: "Lucida Console", "Courier New", monospace;
text-align: center;
margin-left:14mm;
height:4mm;
width:16mm;
}
.imgwrapper{
float: left;
  width: 100%;
  text-align:center;
  height:5px;
}
.column {
  float:left;
  width: 50%; 
  padding: 1px;
  /*margin-left:50px;*/
  /* height: 305px;  Should be removed. Only for demonstration */
}
/* Responsive columns */
@media screen and (max-width: 600px) {
  .column {
    width: 100%;
    display: block;
    margin-left: 10px
    margin-bottom: 10px
  }
}

/* Style the counter cards */
.card {
    border: solid 1px red;
    height: 40mm;
    width: 90mm;
    margin-bottom:3.0mm;
    margin-top:3.0mm;
    margin-left:2mm;
     margin-right:0mm;
    padding: 1px;
    background-color: #a8003a;
}
.p.ex1 {
  margin-top: 0px;
  text-align: center;
  font-size: 14px;
}
</style>
</head>

<body>
<div class="row">
  {% for item in doc.item_tag_details %}
  <div class="column">
  <div class="card">
    <div class="row">
      <div class="column">
          <div class="imgwrapper">
        <img src="/files/Logo_white.png" width="60" height="50">
        <!--<p class="p.ex1">-->
        <!--<img class="a" src="https://qrcode.tec-it.com/API/QRCode?data=https://erp.premiumcellars.ae/app/website-item/{{item.website_item_name}}"/>-->
        <!--</p>-->
        {% set barcode = frappe.db.get_value("Item Barcode",{"parent":item.item_code} , "barcode") %}
        <p class="p.ex1">
            <img class="c" alt='Barcode {{doc.name}}'src='https://barcode.tec-it.com/barcode.ashx?data={{barcode or item.item_code}}&code=Code128&multiplebarcodes=false&translate-esc=true&unit=Fit&dpi=96&imagetype=Gif&rotation=0&color=%23000000&bgcolor=%23ffffff&codepage=Default&qunit=Mm&quiet=0'?draggable="false"/>
        </p>
        <p class="p5">{{item.item_code}}</p> 
        </div>
      </div>
      <div class="column">
          <div class=imgwrapper>
              <!--{% set image = frappe.db.get_value("Item",{"name":item.item_code} , "image") or "/files/LIQUOR1.png" %}-->
        <!--<p class="p">{{item.description |upper}}</p>-->
        <p class="p">{{item.description }}<br>({{item.uom }})</p> <br>
        <!--<img class="b" src="{{image}}">-->
        {% set itemprice = frappe.db.get_value("Item Price",{"item_code":item.item_code,"price_list":"Retail Shop Selling"} , "price_list_rate") %}
        <!--<p class="p2" style="text-align: center;">PRICE</p>-->
        <p style="font-size:17pt;font-weight:bold;color:#e8e80b;text-align: center;">AED &nbsp;&nbsp;{{ "%.2f"|format(item.price|float) or 0.00}} </p>
        <p style="font-size:12pt;font-weight:bold;color:#e8e80b;text-align: center;">(Incl 5℅ vat)* </p>
        </div>
         </div>
    </div>
    </div>
  </div>

  {%- endfor -%} </div>

</body>
</html>

