[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "."}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<!DOCTYPE html>
<html>
<head>
<meta name="viewport" content="width=device-width, initial-scale=1">
<style>
* {
  /*box-sizing: border-box;*/
}

body {
  font-family: Arial, Helvetica, sans-serif; 
}

/* Float four columns side by side */
.column {
  float: left;
  width: 50%;
  padding: 0 1px;
 margin-bottom:12px;
}

/* Remove extra left and right margins, due to padding */
.row {margin: 0 -1px;}

.a {
  background:#ffffff;
  padding:1px;
  margin:1px 0;
  height:80px;
  width:auto;
  text-align:center;
}
.b{
  text-align:center;
  height:85px;
  width:auto;
  margin:0px 0;
}
.c{
 height:50px;
  background:#ffffff;
  padding:1px;
}

.p{
font-family: "Arial Narrow, sans-serif", "Courier New", monospace;
color:  #000000;
font-size: 13pt;
text-align: center;
height:120px;
width:auto;
}
.p2{
font-family: "Lucida Console", "Courier New", monospace;
color:  #000000;
font-size: 10pt;
text-align: left;
/*text-transform: uppercase;*/
height:20px;
width:auto;
}
.p3{
font-family: "Stencil Std, fantasy";
font-weight: bold;
color:  Red;
text-transform: uppercase;
font-size: 14pt;
text-align: left;
height:15px;
width:auto;
}
.p4{
font-family: "Stencil", "Courier New", monospace;
color:  Red;
font-size: 14pt;
text-align: left;
height:10px;
width:auto;
}
.p5{
font-family: "Lucida Console", "Courier New", monospace;
color:  #000000;
font-size: 8pt;
text-align: center;
height:8px;
width:auto;
}
.imgwrapper{
float: left;
  width: 100%;
  text-align:center;
  height:40px;
}
.column {
  float:left;
  width: 50%; 
  padding: 0px;
  /* height: 305px;  Should be removed. Only for demonstration */
}
/* Responsive columns */
@media screen and (max-width: 600px) {
  .column {
    width: 100%;
    display: block;
    margin-bottom: 5px
  }
}

/* Style the counter cards */
.card {
    border: solid 0px white;
    height: 70mm;
    width: 85mm;
    margin-left:20px;
    margin-bottom:5px;
    padding: 0px;
    background-color: #F1F3F4;
}
.p.ex1 {
  margin-top: 0px;
  text-align: center;
  font-size: 12px;
}
</style>
</head>

<body>
<div class="row">
  {% for item in doc.item_tag_details %}
  <div class="column">
  <div class="card">
    <div class="row">   
      <div class="column">
          <div class="imgwrapper">
     <img src="/files/Premium-Cellars2.png" >
        <p class="p.ex1">
        <img class="a" src="https://qrcode.tec-it.com/API/QRCode?data=https://erp.premiumcellars.ae/app/website-item/{{item.website_item_name}}"/>
        </p>
        {% set barcode = frappe.db.get_value("Item Barcode",{"parent":item.item_code,"posa_uom":"Pcs"} , "barcode") %}
        <p class="p.ex1">
            <img class="c" alt='Barcode {{doc.name}}'src='https://barcode.tec-it.com/barcode.ashx?data={{barcode or item.item_code}}&code=Code128&multiplebarcodes=false&translate-esc=true&unit=Fit&dpi=96&imagetype=Gif&rotation=0&color=%23000000&bgcolor=%23ffffff&codepage=Default&qunit=Mm&quiet=0'?draggable="false"/>
        </p>
         <p class="p5">{{item.item_code}}</p> 
        </div>
      </div>
      <div class="column">
          <div class=imgwrapper>
              {% set image = frappe.db.get_value("Item",{"name":item.item_code} , "image") or "/files/LIQUOR1.png" %}
        <p class="p">{{item.description }}<br>({{item.uom }})</p> 
                {% set price = frappe.db.get_value("POS Offer",{"item":item.item_code} , "discount_percentage") or 0.00 %}
                {% set price2 = item.price-price*item.price/100 %}
                <p class="p3">Offer Price</p>
                <p class="p4">AED {{ price2 }}0</p><br>
        <!--<p class="p2" style="text-align: center;">Before :</p>-->
        <p style="font-size:9pt;font-weight:bold;color:green;text-align: left;">Before : AED {{ item.price or 0.00}}</p>
        </div>
        <!-- <p class="p">{{item.item_code}}</p> -->
      </div>
    </div>
    </div>
  </div>

  {%- endfor -%} </div>

</body>
</html>

"}]
